wb = openpyxl.load_workbook(path)
ws = wb['Sheet1']
row_count = ws.max_row
column_count = ws.max_column

copy_wb = openpyxl.load_workbook(copy_path)
copy_ws = copy_wb['Sheet1']

for row_flag in range(1,row_count+1):
    for column_flag in range(1,column_count+1):
        cell_value = ws.cell(row = row_flag,column=column_flag).value
        if cell_value == None:
            copy_ws.cell(row=row_flag, column=column_flag, value=None)
        else:
            if row_flag >= 2:
                index = org_code_list.index(cell_value)
                real_cell_value = org_name_list[index]
                copy_ws.cell(row=row_flag, column=column_flag, value=real_cell_value)
            else:
                copy_ws.cell(row=row_flag, column=column_flag,value=cell_value)

copy_wb.save(copy_path)
